INSERT INTO `config_info` VALUES (648, 'brave-bruid.yml', 'DEFAULT_GROUP', 'spring:\n  datasource:\n    druid:\n      # 数据源基本配置\n      url: jdbc:mysql://127.0.0.1:13306/brave?useUnicode=true&characterEncoding=UTF-8&zeroDateTimeBehavior=convertToNull&allowMultiQueries=true\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      username: root\n      password: root\n      #初始化时建立物理连接的个数\n      initial-size: 5\n      #最大连接池数量\n      max-active: 30\n      #最小连接池数量\n      min-idle: 5\n      #获取连接时最大等待时间，单位毫秒\n      max-wait: 60000\n      #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\n      time-between-eviction-runs-millis: 60000\n      #连接保持空闲而不被驱逐的最小时间\n      min-evictable-idle-time-millis: 300000\n      #用来检测连接是否有效的sql，要求是一个查询语句\n      validation-query: SELECT 1 FROM DUAL\n      #建议配置为true，不影响性能，并且保证安全性。申请连接的时候检测，如果空闲时间大于timeBetweenEvictionRunsMillis，执行validationQuery检测连接是否有效。\n      test-while-idle: true\n      #申请连接时执行validationQuery检测连接是否有效，做了这个配置会降低性能\n      test-on-borrow: false\n      #归还连接时执行validationQuery检测连接是否有效，做了这个配置会降低性能。\n      test-on-return: false\n      #是否缓存preparedStatement，也就是PSCache。PSCache对支持游标的数据库性能提升巨大，比如说oracle。在mysql下建议关闭\n      pool-prepared-statements: false\n      #要启用PSCache，必须配置大于0，当大于0时，poolPreparedStatements自动触发修改为true。\n      #max-pool-prepared-statement-per-connection-size: 50\n      #配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙\n      filters: config,stat,wall,slf4j\n      #通过connectProperties属性来打开mergeSql功能；慢SQL记录\n      connect-properties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=500\n      #合并多个DruidDataSource的监控数据\n      use-global-data-source-stat: false\n      #配置DruidStatViewServlet\n      stat-view-servlet:\n        #是否启用StatViewServlet（监控页面）默认值为false（考虑到安全问题默认并未启动，如需启用建议设置密码或白名单以保障安全）\n        enabled: true\n        url-pattern: /druid/*\n        login-username: brave\n        login-password: brave\n        #IP白名单\n        allow: 127.0.0.1,192.168.1.113\n        #IP黑名单（优先级高于IP白名单）\n        deny:\n        #禁用HTML页面上的“Reset All”功能\n        reset-enable: false\n      #配置DruidStatFilter\n      web-stat-filter:\n        #是否启用StatFilter默认值false\n        enabled: true\n        url-pattern: /*\n        exclusions: /druid/*,*.js,*.gif,*.jpg,*.bmp,*.png,*.css,*.ico', 'e5f324e79f00f5d4fd117eaaa25aa6e3', '2021-08-12 06:19:49', '2021-08-12 07:17:46', 'nacos', '172.18.0.1', '', 'ecfd1e10-0d45-4fb0-b4a8-74b77c4f7e65', 'brave-bruid数据源配置', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (649, 'brave-minio.yml', 'DEFAULT_GROUP', 'spring:\n  servlet:\n    multipart:\n      enabled: true\n      max-file-size: 10MB\n      max-request-size: 100MB\nminio:\n  endpoint: http://127.0.0.1:9000\n  accessKey: minio\n  secretKey: minioadmin\n  bucketName: brave', 'a3fb65012af00db3ade57850df1decfc', '2021-08-17 06:56:31', '2021-08-17 06:56:51', 'nacos', '172.18.0.1', '', 'ecfd1e10-0d45-4fb0-b4a8-74b77c4f7e65', 'Brave Minio 配置文件', '', '', 'yaml', '');
